package com.edu;

public class BitwiseOperator {

	public static void main(String[] args) {
		int a = 4;
		int b = 5;
	    int c = a^b;
	    int d = a&b;
	    int e = a|b;
	    int f = ~a;
	    int g = ~b;
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);
		System.out.println(g);
		// TODO Auto-generated method stub

	}

}
